import React, { useState } from 'react'
import { SavingProps } from '../types/saving'
import "../styles/saving.css"
import AddTaskIcon from '@mui/icons-material/AddTask';
import { Box, TextField, Button, List, ListItem, styled } from '@mui/material'
import CircularProgress from '@mui/material/CircularProgress';
import Typography from '@mui/material/Typography';


const Saving = ({saving }: SavingProps) => {
  const [target, setTarget] = useState(0)
  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
e.preventDefault()
  }
  return (
      <Box
    component="form"
    autoComplete="off"
    onSubmit={(e)=>onSubmit(e)}
    sx={{display: "flex", flexDirection: "column",  gap: 0.5}}
    >
      <h4>Current saving: {saving}</h4>
      <h4>Current target: {target}</h4>
      <CircularProgress variant="determinate" value={saving/target*100} />
      <Box
        sx={{
          top: 0,
          left: 0,
          bottom: 0,
          right: 0,
          position: 'absolute',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
        }}
      ></Box>

      <Typography
          variant="caption"
          component="div"
          color="text.secondary"
        >{`${Math.round(saving/target*100)}%`}</Typography>

      <TextField
          label= {`Set target`}
          variant="standard"
          onChange={(e)=>setTarget(Number(e.target.value))}
          type="number"

        />

    <Button 
        type='submit'
        variant="outlined"
        startIcon={<AddTaskIcon />}
        color="secondary"
        size="small"
        className="save-btn"

        >Set target</Button>
  </Box>
  )
}

export default Saving
