reset({})
  }

 let inputRef: HTMLInputElement | null = null

  return (
    <div>
      <div className="App">
        <Money option='Income' list={incomes} setList={setIncomes}/>
        <Money option='Expense' list={expenses} setList={setExpenses}/>
        <Saving saving={saving}/>
      </div>
      <div>
        <Balance balance={balance} setSaving={setSaving}/>
      </div>

<form onSubmit={handleSubmit(onSubmit)}>
  <div>
    <label htmlFor = "firstname">Enter your first name</label>
    <input type= "text" id="firstname" {...register("firstname", {
      required: true,
      minLength: 5,
      maxLength: 20,
      pattern: /([A-Z])\w+/g
    })
  }/>
  </div>

  <div>
    <label htmlFor = "lastname">Enter your last name</label>
    <input type="text" id="lastname" {...register("lastname")} />
  </div>

  <div>
    <label htmlFor = "email">Enter your email</label>
    <input type="text" id="email" {...register("email")}/>
  </div>

  <div>
    <label htmlFor = "password">Enter your password</label>
    <input type="password" id="password" {...register("password")} />
  </div>

  <div>
    <label htmlFor = "re_password">Confirm your password</label>
    <input type="password" id="re_password" {...register("re_password")} />
  </div>

  <div>
   <select id="gender" {...register("gender")} >
    <option value="male">Male</option>
    <option value="female">Female</option>
    <option value="others">Others</option>
   </select>
  </div>

<div>
  <label htmlFor="subscribe">Do you want to subscribe?</label>
  <input type="checkbox" id="subscribe" {...register("subscribe")} />
</div>

  <button type='submit'>Save data</button>
</form>

</div>
