  }

 let inputRef: HTMLInputElement = null

  return (
    <div>
      <div className="App">
        <Money option='Income' list={incomes} setList={setIncomes}/>
        <Money option='Expense' list={expenses} setList={setExpenses}/>
        <Saving saving={saving}/>
      </div>
      <div>
        <Balance balance={balance} setSaving={setSaving}/>
      </div>

<form onSubmit={(e) => onSubmit(e)}>
  <div>
    <label htmlFor = "firstname">Enter your first name</label>
    <input type="text" name="firstname" id="firstname" defaultValue="Alia" ref={(input) => inputRef = input}/>
  </div>

  <div>
    <label htmlFor = "lastname">Enter your last name</label>
    <input type="text" name="lastname" id="lastname" />
  </div>

  <div>
    <label htmlFor = "email">Enter your email</label>
    <input type="text" name="email" id="email" />
  </div>

  <div>
    <label htmlFor = "password">Enter your password</label>
    <input type="password" name="password" id="password" />
  </div>

  <div>
    <label htmlFor = "re-password">Confirm your password</label>
    <input type="password" name="re-password" id="re-password" />
  </div>

  <div>
   <select name="gender" id="gender">
    <option value="male">Male</option>
    <option value="female">Female</option>
    <option value="others">Others</option>
   </select>
  </div>

<div>
  <label htmlFor="subscribe">Do you want to subscribe?</label>
  <input type="checkbox" name="subscribe" id="subscribe" />
</div>

  <button type='submit'>Save data</button>
</form>

</div>
