import { ClientRequest } from 'http'
import React, { useState } from 'react'
import { MoneyProps } from '../types/money'
import { Box, TextField } from '@mui/material'

const Money = ({option, list, setList}: MoneyProps) => {
    const [title, setTitle] = useState("")
    const [amount, setAmount] = useState(0)
    const [date, setDate] = useState("")
    const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
      e.preventDefault()
        setList([{amount, date, title, id: Date.now() }, ...list])
    }

  return (
    <Box
    component="form"
    autoComplete="off"
    >
        <form onSubmit={(e)=>onSubmit(e)}>
        <TextField
        required
          label= {`${option} Source`}
          variant="standard"
          onChange={(e)=> setTitle(e.target.value)}
          value={title}
          type="text"
        />
        <TextField
        required
          label= {`Amount of ${option}`}
          variant="filled"
          onChange={(e) => setAmount(Number(e.target.value))}
          type="number"
          InputLabelProps={{
            shrink: true,
          }}
        />
        <TextField
        required
          label= {`Date of ${option}`}
          variant="filled"
          onChange={(e) => setDate(e.target.value)}
          value={date}
          type="date"
          InputLabelProps={{
            shrink: true,
          }}
        />
        <button type='submit'>Save</button>
        </form>
        <ul>
    {
        list.length > 0 && list.map(
            item => (
                <li key={item.id}> {item.title} , {item.amount} , {item.date}</li>
            )
        ) 
    }
        </ul>
    </Box>
  )
}

export default Money
