import React from 'react'
import { BalanceProps } from '../types/balance'

const Balance = ({balance, setSaving}:BalanceProps) => {
  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
e.preventDefault()
setSaving((saving)=>saving)
  }
  return (
    <div>
      <p>{balance}</p>
      <form onSubmit={(e)=> onSubmit(e)}>
        <label htmlFor="addSaving">Add to saving account</label>
        <input type="number" name="addSaving" id="addSaving" />
        <button type="submit">Transfer</button>
      </form>
    </div>
  )
}

export default Balance
