import React, { useState } from 'react'
import '../styles/balance.css'
import { BalanceProps } from '../types/balance'
import AddTaskIcon from '@mui/icons-material/AddTask';

const Balance = ({ balance, setSaving}: BalanceProps) => {
  const [amount, setAmount] = useState(0)
  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
e.preventDefault()
setSaving((saving) => saving + amount)

  }
  return (
    <div className = 'balance'>
      <p id= "header">Current balance {balance}</p>
      <form onSubmit={(e)=> onSubmit(e)}>
      <TextField
      requierd
          label= {`Add to saving account`}
          variant="standard"
          onChange= {(e) => setAmount(Number(e.target.value))}
          type="number"
        />

        <label htmlFor="addSaving">Add to saving account</label>
        <input type="number" name="addSaving" id="addSaving"  />


        <button type="submit">Transfer</button>
      </form>
    </div>
  )
}

export default Balance
